import { useState } from "react";

const Home = () => {
    const cards =  [
        { Title: '24/7 Support', body: 'Organizations and individuals are seeking fast, safe, easy, and cheap. Organizations and individuals are seeking fast, safe, easy, and cheap',},
        { Title: 'Low Charges', body: 'Organizations and individuals are seeking fast, safe, easy, and cheap.Organizations and individuals are seeking fast, safe, easy, and cheap',},
        { Title: 'Service Availability', body: 'Organizations and individuals are seeking fast, safe, easy, and cheap.Organizations and individuals are seeking fast, safe, easy, and cheap',},
        { Title: 'Bank Account Management', body: 'Organizations and individuals are seeking fast, safe, easy, and cheap.Organizations and individuals are seeking fast, safe, easy, and cheap',},
        { Title: 'Easy Navigating Platform', body: 'Organizations and individuals are seeking fast, safe, easy, and cheap.Organizations and individuals are seeking fast, safe, easy, and cheap',},
        { Title: 'Contactless Payment', body: 'Organizations and individuals are seeking fast, safe, easy, and cheap.Organizations and individuals are seeking fast, safe, easy, and cheap',},
    ]

    //let white = '#fff'
    // const [color, setColor] = useState(white)

    // const colorChanger=() =>{
    //     let green = '#8fb874'
    //     setColor(green)
    // }
    return ( <section>
        <div className="about-e p-10">
            <h2 className="text-3xl">About eNaira</h2>
            <p>eNaira is a Central Bank of Nigeria-issued digital currency that provides a unique form of money denominated in Naira. eNaira serves as both a medium of exchange and a store of value, offering better payment prospects in retail transactions when compared to cash payments. eNaira has an exclusive operational structure that is both remarkable and nothing like other forms of central bank money.</p>
            <p>There are several benefits from a central bank-issued digital currency in Nigeria, and this cuts across different sectors of, and concerns of the economy. eNaira is an electronic version of the local paper naira currency, equal in value and issued by the Central Bank Nigeria. It functions as a safe and efficient alternative means of payment.</p>
            <p>Organizations and individuals are seeking fast, safe, easy, and cheap means of payment. eNaira was minted to meet these pressing needs.</p>
        </div>
        <section>
            <div className="flex justify-between p-10 text-2xl">
            <h1>Key Features</h1>
            <button className="border px-5 py-2 text-sm">More Features</button>
            </div>


            <div className="features grid grid-cols-3 gap-5 p-20">
                {cards.map((card, index)=>(
                <div className="border rounded-3xl shadow-md p-5 text-sm">
                    <div className=" w-10 h-10 bg-gray-300 rounded-full flex justify-center">
                    <i class="fa fa-money pt-3"></i>   
                    </div>
                    <h3 className="font-bold my-5">{card.Title}</h3>
                    <p>{card.body}</p> 
                </div>
                ))}
            </div>
        </section>
        <section>
            <h1>Why use eNaira</h1>
            <p>All the amazing benefits we offer our customers using eNaira</p>
            <div>

            </div>
        </section>
    </section> );
}

export default Home;
