import { useState } from "react";
const FaqDrop = ({question, answer}) => {
    const [openModal, setOpenModal] = useState(false)

    const dropModal = ()=>{
        !openModal ? setOpenModal(true) : setOpenModal(false)
    }

    const ans = <div>
        <p>{answer}</p>
    </div>
    return ( 
        <div>
            <div className={openModal ? "faq-drop-header bg-red-500 rounded-tr-sm rounded-tl-sm text-white" : "faq-drop-header"} onClick={dropModal} >
                   <p className='flex gap-2 justify-between items-center cursor-pointer p-3'>{question}<i className='fa fa-plus' style={!openModal ? 'z-10' : 'z-0'}  width={20} /> </p>

               </div>

               {openModal? ans: ''}
        </div>
     );
}

export default FaqDrop;
